import React, { useRef, useState } from "react";
import axios from "axios";
import "../src/css/wit.css";
import "../src/css/witc.css";
import Load from "./Load";
function Wit() {
  const [message, setmessage] = useState();
  const [loading, setLoading] = useState(false);
  const [wc, setwc] = useState(false);
  const wamnt = useRef();
  const wupi = useRef();
  const wac = async () => {
    try {
      setLoading(true);
      const un = sessionStorage.getItem("un");
      await axios
        .post("http://localhost:4000/wd", {
          username: un,
          amount: wamnt.current.value,
        })
        .then((res) => {
          if (res.data.status === "ib") {
            setmessage("INSUFFICIENT BALANCE");
          }
          if (res.data.status === "success") {
            setmessage("PAYOUT SUCCESSFULL");
          } else {
            setmessage(res.data.status);
          }
        });
    } catch (error) {
      setmessage(error.message);
    } finally {
      setLoading(false);
    }
  };
  const wa = async () => {
    if (wupi.current.value === "") {
      if (wamnt.current.value === "") {
        setmessage("SOME FIELD IS EMPTY");
      } else {
        setmessage("SOME FIELD IS EMPTY");
      }
    } else {
      if (wamnt.current.value === "") {
        setmessage("SOME FIELD IS EMPTY");
      } else {
        if (wamnt.current.value < 20) {
          setmessage("MINIMUM MITHDRAW IS 20");
        } else {
          setwc(true);
        }
      }
    }
  };
  return wc ? (
    <div className="wcmain">
      {loading ? (
        <Load />
      ) : (
        <div className="wcbody">
          <div>ARE YOU SURE TO WITHDRAW 20</div>
          <button onClick={wac}>YES</button>
          <button>NO</button>
          <div>MESSAGE</div>
        </div>
      )}
    </div>
  ) : (
    <div className="wmain">
      <div className="wbody">
        <div className="wh">WITHDRAW</div>
        <input
          id="wupi"
          type="text"
          placeholder="ENTER UPI ID "
          className="lass"
          ref={wupi}
        />
        <div id="we" className="lass">
          <span>Eg : </span>123456789@paytm
        </div>
        <input
          className="lass"
          type="number"
          placeholder="ENTER THE AMOUNT"
          ref={wamnt}
        />
        <button className="lass" id="wsubmit" onClick={wa}>
          SUBMIT
        </button>
        <div className="lass" id="wstatus">
          {message}
        </div>
      </div>
    </div>
  );
}
export default Wit;
