import React, { useState } from "react";
import "../src/css/dep.css";
import "../src/css/upi.css";
function Dep() {
  const [amount, setamount] = useState();
  const [upi, setupi] = useState(false);
  const twenty = () => {
    setupi(true);
    setamount(20);
  };
  const fifty = () => {
    setamount(50);
  };
  const hundred = () => {
    setamount(100);
  };
  const fivehundred = () => {
    setamount(500);
  };
  const thousand = () => {
    setamount(1000);
  };
  const twothousand = () => {
    setamount(2000);
  };
  const uclose=()=>{
    setupi(false);
  }
   return upi ? (
    <div className="upi">
      <div className="iupi">
        <form>
          <div id="upia">
            AMOUNT : <span>{amount}</span>
          </div>
          <div>
            <input
              className="uss"
              type="text"
              placeholder="ENTER YOUR UPI ID"
            />
          </div>
          <div>
            <button type="submit" className="uss" id="upisubmit">
              SUBMIT
            </button>
            <button className="uss" id="upiclose" onClick={uclose}>CLOSE</button>
          </div>
          <div id="upis" className="uss">
            STATUS
          </div>
        </form>
      </div>
    </div>
  ) : (
    <div className="dmain">
      <div className="dbody">
        <div className="dh">DEPOSIT</div>
        <br />
        <button onClick={twenty} className="mdb">
          20
        </button>

        <button className="db" onClick={fifty}>
          50
        </button>
        <button className="db" onClick={hundred}>
          100
        </button>
        <br />
        <button onClick={fivehundred} className="mdb">
          500
        </button>
        <button className="db" onClick={thousand}>
          1000
        </button>
        <button className="db" onClick={twothousand}>
          2000
        </button>
      </div>
    </div>
  );
}
export default Dep;
