import React, { useState } from "react";
import axios from "axios";
import {
  CardElement,
  useStripe,
  useElements,
  Elements,
} from "@stripe/react-stripe-js";
import { loadStripe } from "@stripe/stripe-js";
import "../src/css/dep.css";
const stripePromise = loadStripe(
  "pk_test_51OR8kiSIm5G6fCOH1qQFbjSNZvv8qnS9ZEP6fMmJbeUCDKxN45UHHAEoajkpRjpNiO4Jn6s5Jl4FeUvbJEGr42QG00k4Oz2rEA"
);
function Depositform() {
  const stripe = useStripe();
  const elements = useElements();
  const [amount, setamount] = useState(20);
  const deposit = async (event) => {
    event.preventDefault();
    if (!stripe || !elements) {
      return;
    }
    const cardElement = elements.getElement(CardElement);
    const { error, paymentMethod } = await stripe.createPaymentMethod({
      type: "card",
      card: cardElement,
    });

    if (error) {
      console.error(error);
    } else {
      const response = await axios.post("http://localhost:4000/dep", {
        payment_method_id: paymentMethod.id,
        amount: amount,
      });
      if (response.data.success) {
        console.log(response.data.message);
      } else {
        console.log(response.data.message);
      }
    }
  };
  const twenty = async () => {
    await setamount(20);
    await deposit();
  };
  const fifty = () => {
    setamount(50);
    deposit();
  };
  const hundred = () => {
    setamount(100);
    deposit();
  };
  const fivehundred = () => {
    setamount(500);
    deposit();
  };
  const thousand = () => {
    setamount(1000);
    deposit();
  };
  const twothousand = () => {
    setamount(2000);
    deposit();
  };
  return (
    <div className="dmain">
      <div className="dbody">
        <form onSubmit={deposit}>
          <CardElement />
          <div className="dh">DEPOSIT</div>
          <br />
          <button type="submit" onClick={twenty} disabled={!stripe}>
            20
          </button>

          <button
            className="db"
            type="submit"
            onClick={fifty}
            disabled={!stripe}
          >
            50
          </button>
          <button
            className="db"
            type="submit"
            onClick={hundred}
            disabled={!stripe}
          >
            100
          </button>
          <br />
          <button onClick={fivehundred} type="submit" disabled={!stripe}>
            500
          </button>
          <button
            className="db"
            type="submit"
            onClick={thousand}
            disabled={!stripe}
          >
            1000
          </button>
          <button
            className="db"
            type="submit"
            onClick={twothousand}
            disabled={!stripe}
          >
            2000
          </button>
        </form>
      </div>
    </div>
  );
}
function Dep() {
  return (
    <Elements stripe={stripePromise}>
      <Depositform />
    </Elements>
  );
}
export default Dep;
