import React, { useRef, useState } from "react";
import axios from "axios";
import "../src/css/wit.css";
import Load from "./Load";
function Wit() {
  const [message, setmessage] = useState();
  const [loading, setLoading] = useState(false);
  const wamnt = useRef();
  const ws = async () => {
    if (wamnt.current.value < 20) {
      setmessage("MINIMUM MITHDRAW IS 20");
    } else {
      try {
        setLoading(true);
        const un = sessionStorage.getItem("un");
        await axios
          .post("http://localhost:4000/wd", {
            username: un,
            amount: wamnt.current.value,
          })
          .then((res) => {
            if (res.data.status === "ib") {
              setmessage("INSUFFICIENT BALANCE");
            }
            if (res.data.status === "success") {
              setmessage("PAYOUT SUCCESSFULL");
            } else {
              setmessage(res.data.status);
            }
          });
      } catch (error) {
        setmessage(error.message);
      } finally {
        setLoading(false);
      }
    }
  };
  return (
    <div className="wmain">
      {loading ? (
        <Load />
      ) : (
        <div className="wbody">
          <div className="wh">WITHDRAW</div>
          <input
            id="wupi"
            type="text"
            placeholder="ENTER UPI ID "
            className="lass"
          />
          <div id="we" className="lass">
            <span>Eg : </span>123456789@paytm
          </div>
          <input
            className="lass"
            type="number"
            placeholder="ENTER THE AMOUNT"
            ref={wamnt}
          />
          <button className="lass" id="wsubmit" onClick={ws}>
            SUBMIT
          </button>
          <div className="lass" id="wstatus">
            {message}
          </div>
        </div>
      )}
    </div>
  );
}
export default Wit;
