import React, { useRef, useState } from "react";
import "./css/log.css";
import axios from "axios";
function Log() {
  const suname = useRef(null);
  const spass = useRef(null);
  const sphone = useRef(null);
  const [message1, setmessage1] = useState();
  const [message2, setmessage2] = useState();
  const [acc, setacc] = useState();
  const [cuser,setcuser]=useState();
  const cu=async(event)=>{
    event.preventDefault();
    await axios.post("http://localhost:4000/cu",{
      suname:suname.current.value,
    }).then((res)=>{
      if(res.data==="a"){
        setcuser("USERNAME IS AVAILABLE");
      }
      else{
        setcuser("USERNAME IS NOT AVAILABLE");
      }
    })
  }
  const sp = async (event) => {
    event.preventDefault();
    await axios
      .post("http://localhost:4000/signup", {
        suname: suname.current.value,
        spass: spass.current.value,
        sphone: sphone.current.value,
      })
      .then((res) => {
        if (res.data === "permit") {
          console.log("permit");
          setacc("permit");
          setmessage1("SIGN-UP SUCCESSFULL");
          setmessage2("GO TO LOGIN");
        }
      });
  };
  return (
    <div className="lbody">
      <div className="main">
        <input type="checkbox" id="chk" aria-hidden="true" />
        <div className="signup">
          <form>
            <label htmlFor="chk" aria-hidden="true">
              LOGIN
            </label>
            <input
              className="linput"
              type="text"
              name="username"
              placeholder="USER NAME"
              required=""
            />
            <input
              className="linput"
              type="password"
              name="password"
              placeholder="PASSWORD"
              required=""
            />
            <button id="lbutton">LOGIN</button>
          </form>
        </div>
        {acc !== "permit" ? (
          <div className="login">
            <form onSubmit={sp}>
              <label htmlFor="chk" aria-hidden="true">
                SIGN UP
              </label>
              <input
                id="suinput"
                type="text"
                name="username"
                placeholder="USER NAME"
                required=""
                ref={suname}
              />
              <button id="ca" onClick={cu}>CHECK</button>
              <h6 id="am">{cuser}</h6>
              <input
                id="spinput"
                type="password"
                name="password"
                placeholder="PASSWORD"
                required=""
                ref={spass}
              />
              <input
                id="sninput"
                type="number"
                name="phone"
                placeholder="PHONE NUMBER"
                required=""
                ref={sphone}
              />
              <button id="sbutton">SIGN UP</button>
              <div>{message1}</div>
            </form>
          </div>
        ) : (
          <div className="login">
            <form onSubmit={sp}>
              <label htmlFor="chk" aria-hidden="true">
                SIGN UP
              </label>
              <div id="amess">
                {message1}
                <br></br>
                {message2}
              </div>
            </form>
          </div>
        )}
      </div>
    </div>
  );
}
export default Log;
