import React, { useRef, useState } from "react";
import axios from "axios";
import * as Cashfree from 'cashfree-sdk';
import "../src/css/dep.css";
import "../src/css/upi.css";
function Dep() {
  const apiid="TEST101096810227a7f218ceece85db618690101";
  const secretid="cfsk_ma_test_5aba9f824253b93c601ff2dda9107897_2b65656b";
  const [amount, setamount] = useState();
  const [upi, setupi] = useState(false);
  const upiid = useRef();
  const upipayment =  () => {
    window.open("https://buy.stripe.com/test_14k3cF6yG1Qa26QaEE", '_blank');
  };
  const twenty = () => {
    setupi(true);
    setamount(20);
  };
  const fifty = () => {
    setupi(true);
    setamount(50);
  };
  const hundred = () => {
    setupi(true);
    setamount(100);
  };
  const fivehundred = () => {
    setupi(true);
    setamount(500);
  };
  const thousand = () => {
    setupi(true);
    setamount(1000);
  };
  const twothousand = () => {
    setupi(true);
    setamount(2000);
  };
  const uclose = () => {
    setupi(false);
  };
  return upi ? (
    <div className="upi">
      <div className="iupi">
        <form>
          <div id="upia">
            AMOUNT : <span>{amount}</span>
          </div>
          <div>
            <input
              className="uss"
              type="text"
              placeholder="ENTER YOUR UPI ID"
              ref={upiid}
            />
          </div>
          <div>
            <button
              type="submit"
              className="uss"
              id="upisubmit"
              onClick={upipayment}
            >
              SUBMIT
            </button>
            <button className="uss" id="upiclose" onClick={uclose}>
              CLOSE
            </button>
          </div>
          <div id="upis" className="uss">
            STATUS
          </div>
        </form>
      </div>
    </div>
  ) : (
    <div className="dmain">
      <div className="dbody">
        <div className="dh">DEPOSIT</div>
        <br />
        <button onClick={twenty} className="mdb">
          20
        </button>

        <button className="db" onClick={fifty}>
          50
        </button>
        <button className="db" onClick={hundred}>
          100
        </button>
        <br />
        <button onClick={fivehundred} className="mdb">
          500
        </button>
        <button className="db" onClick={thousand}>
          1000
        </button>
        <button className="db" onClick={twothousand}>
          2000
        </button>
      </div>
    </div>
  );
}
export default Dep;
