import React, { useEffect, useRef, useState } from "react";
import "../src/css/bgmi.css";
import axios from "axios";
import "../src/css/pay.css";
import { Link, useNavigate } from "react-router-dom";
function Bgmi({ bs1, bs2, bsd }) {
  const [jstate, setjstate] = useState(false);
  const [pamount, setpamount] = useState();
  const [gtype, setgtype] = useState();
  const [jsbn1, setjsbn1] = useState(false);
  const [jsbn2, setjsbn2] = useState(false);
  const [jsdbn, setjsdbn] = useState(false);
  const [pstatus, setpstatus] = useState();
  const gname = useRef(null);
  const [bsc1, setbsc1] = useState();
  const [bsc2, setbsc2] = useState();
  const [bsdc, setbsdc] = useState();
  const [joined, setjoined] = useState();
  const [ps, setps] = useState(false);
  const navigate = useNavigate();
  useEffect(() => {
    const bjstatus = async () => {
      const un = sessionStorage.getItem("un");
      await axios
        .post("http://localhost:4000/bjstatus", {
          username: un,
        })
        .then((res) => {
          if (res.data.count[0] < 100) {
            setbsc1(res.data.count[0]);
          } else {
            setjsbn1(true);
            setbsc1(res.data.count[0]);
            setjoined("MATCH FULL");
          }
          if (res.data.count[1] < 100) {
            setbsc2(res.data.count[1]);
          } else {
            setjsbn2(true);
            setbsc2(res.data.count[1]);
            setjoined("MATCH FULL");
          }
          if (res.data.count[2] < 100) {
            setbsdc(res.data.count[2]);
          } else {
            setjsdbn(true);
            setbsdc(res.data.count[2]);
            setjoined("MATCH FULL");
          }
          if (res.data.bsolo1 === "av") {
            setjsbn1(true);
            setjoined("JOINED");
          }
          if (res.data.bsolo2 === "av") {
            setjsbn2(true);
            setjoined("JOINED");
          }
          if (res.data.bsquad === "av") {
            setjsdbn(true);
            setjoined("JOINED");
          }
        });
    };
    bjstatus();
  }, []);
  const pc = () => {
    setjstate(false);
  };
  const pay = async () => {
    if (gtype === "bsolo1") {
      if (gname.current.value !== "") {
        const un = sessionStorage.getItem("un");
        await axios
          .post("http://localhost:4000/bsolo1", {
            username: un,
            gamename: gname.current.value,
          })
          .then((res) => {
            if (res.data.ws === "success") {
              window.location.reload(false);
              navigate("/bgmi");
            } else {
              setpstatus("LOW FUND RECHARGE YOUR WALLET");
            }
          });
      } else {
        setpstatus("FIELD IS EMPTY");
      }
    }
    if (gtype === "bsolo2") {
      if (gname.current.value !== "") {
        const un = sessionStorage.getItem("un");
        await axios
          .post("http://localhost:4000/bsolo2", {
            username: un,
            gamename: gname.current.value,
          })
          .then((res) => {
            if (res.data.ws === "success") {
              window.location.reload(false);
              navigate("/bgmi");
            } else {
              setpstatus("LOW FUND RECHARGE YOUR WALLET");
            }
          });
      } else {
        setpstatus("FIELD IS EMPTY");
      }
    }
    if (gtype === "bsquad") {
      if (gname.current.value !== "") {
        const un = sessionStorage.getItem("un");
        await axios
          .post("http://localhost:4000/bsquad", {
            username: un,
            gamename: gname.current.value,
          })
          .then((res) => {
            if (res.data.ws === "success") {
              window.location.reload(false);
              navigate("/bgmi");
            } else {
              setpstatus("LOW FUND RECHARGE YOUR WALLET");
            }
          });
      } else {
        setpstatus("FIELD IS EMPTY");
      }
    }
  };
  const bsolo1 = async () => {
    setpamount("10");
    setjstate(true);
    setgtype("bsolo1");
  };
  const bsolo2 = async () => {
    setpamount("10");
    setjstate(true);
    setgtype("bsolo2");
  };
  const bsquad = async () => {
    setpamount("10");
    setjstate(true);
    setgtype("bsquad");
  };
  const bprice = async () => {
    setps(true);
  };
  return jstate ? (
    <div id="pmain">
      <div id="jp">
        <div id="tc">
          <h2 id="phead">TERMS AND CONDITIONS</h2>
          <button id="pc" onClick={pc}>
            X
          </button>
          <ol>
            <li>
              The room id and password will be sent to your registered mobile
              number,Before 10min of the match.
            </li>
            <li>
              The player game level must be 25 or above.If you join the match
              with below level no cash price will be provided.
            </li>
            <li>Do not team up with any players.</li>
            <li>
              The resgistered game name should be same as your real game name,if
              its being different no cash prize will be provided.
            </li>
            <li>Do not use any hacks.</li>
            <li>Do not share the room id and password.</li>
            <li>
              If you have any payment issue after the result of the match,you
              should contact on email within 24hrs because the result of the
              matches will be cleared within 24hrs.
            </li>
            <li>
              If you violate any of these terms and conditions,your account will
              be banned.
            </li>
            <li>No refund policy</li>
          </ol>
        </div>
        <div id="jb">
          <input id="ptext" type="text" placeholder="GAME NAME" ref={gname} />
          <button id="pay" onClick={pay}>
            PAY={pamount}
          </button>
          <div id="pstatus">{pstatus}</div>
        </div>
      </div>
    </div>
  ) : (
    <div className="bmain">
      {ps ? (
        <div className="ps">
          <h4>WINNER : 100</h4>
          <h4>RUNNER : 50</h4>
          <h4>KILL : 5</h4>
          <button>CLOSE</button>
        </div>
      ) : (
        <div className="bdiv" id="bsolo">
          <div className="bimage"></div>
          <div className="bdown1">
            <h4>
              TYPE : <span>{bs1.type}</span>
            </h4>
            <h4>
              MAP : <span>{bs1.map}</span>
            </h4>
            <h4>
              DATE : <span>{bs1.date}</span>
            </h4>
            <h4>
              TIME : <span>{bs1.time}</span>
            </h4>
          </div>
          <div className="bdown2">
            <div id="bp">
              <div id="ibp" style={{ width: `${bsc1}%` }}></div>
            </div>
            <h4 id="bpn">{bsc1}/100</h4>
            <Link className="bl" onClick={bprice}>
              PRICE
            </Link>
            {jsbn1 === false ? (
              <button className="bjoin" onClick={bsolo1}>
                JOIN
              </button>
            ) : (
              <div className="bjoined">{joined}</div>
            )}
          </div>
        </div>
      )}
      <div className="bdiv" id="bduo">
        <div className="bimage"></div>
        <div className="bdown1">
          <h4>
            TYPE : <span>{bs2.type}</span>
          </h4>
          <h4>
            MAP : <span>{bs2.map}</span>
          </h4>
          <h4>
            DATE : <span>{bs2.date}</span>
          </h4>
          <h4>
            TIME : <span>{bs2.time}</span>
          </h4>
        </div>
        <div className="bdown2">
          <div id="bp">
            <div id="ibp" style={{ width: `${bsc2}%` }}></div>
          </div>
          <h4 id="bpn">{bsc2}/100</h4>
          {jsbn2 === false ? (
            <button className="bjoin" onClick={bsolo2}>
              JOIN
            </button>
          ) : (
            <div className="bjoined">{joined}</div>
          )}
        </div>
      </div>
      <div className="bdiv" id="bsquad">
        <div className="bimage"></div>
        <div className="bdown1">
          <h4>
            TYPE : <span>{bsd.type}</span>
          </h4>
          <h4>
            MAP : <span>{bsd.map}</span>
          </h4>
          <h4>
            DATE : <span>{bsd.date}</span>
          </h4>
          <h4>
            TIME : <span>{bsd.time}</span>
          </h4>
        </div>
        <div className="bdown2">
          <div id="bp">
            <div id="ibp" style={{ width: `${bsdc}%` }}></div>
          </div>
          <h4 id="bpn">{bsdc}/100</h4>
          {jsdbn === false ? (
            <button className="bjoin" onClick={bsquad}>
              JOIN
            </button>
          ) : (
            <div className="bjoined">{joined}</div>
          )}
        </div>
      </div>
    </div>
  );
}
export default Bgmi;
