import React, { useState } from "react";
import axios from "axios";
import "../src/css/dep.css";
import "../src/css/upi.css";
import { loadStripe } from "@stripe/stripe-js";
import "../src/Load";
import Load from "../src/Load";
function Dep() {
  const stripePromise = loadStripe(
    "pk_test_51OXnenSC6UDJ5EILprhrs88tyup0P7XW3uwL9YzCWnCkftJLjA8ZYoHqa5UjvK5WBiFQOe5or1UwwVtAPn75rP8p00RVLGCHcH"
  );
  const [amount, setamount] = useState();
  const [upi, setupi] = useState(false);
  const [loading, setLoading] = useState(false);
  const [message, setmessage] = useState();
  const upipayment = async () => {
    setLoading(true);
    try {
      const un = sessionStorage.getItem("un");
      const response = await axios.post("http://localhost:4000/payment", {
        amount: amount,
        username: un,
      });
      const session = await response.data;
      const stripe = await stripePromise;
      const result = await stripe.redirectToCheckout({ sessionId: session.id });

      if (result.error) {
        setmessage(result.error.message);
      }
      if (result.paymentIntent) {
        setmessage("Payment succeeded");
      }
    } catch (error) {
      setmessage(error.message);
    } finally {
      setLoading(false);
    }
  };
  const twenty = () => {
    setupi(true);
    setamount(20);
  };
  const fifty = () => {
    setupi(true);
    setamount(50);
  };
  const hundred = () => {
    setupi(true);
    setamount(100);
  };
  const fivehundred = () => {
    setupi(true);
    setamount(500);
  };
  const thousand = () => {
    setupi(true);
    setamount(1000);
  };
  const twothousand = () => {
    setupi(true);
    setamount(2000);
  };
  const uclose = () => {
    setupi(false);
  };
  return upi ? (
    <div className="upi">
      {loading ? (
        <Load />
      ) : (
        <div className="iupi">
          <div id="upia">
            AMOUNT : <span>{amount}</span>
          </div>
          <div className="uss" id="upit">ARE YOU SURE TO DEPOSIT ?</div>
          <div>
            <button
              type="submit"
              className="uss"
              id="upisubmit"
              onClick={upipayment}
              disabled={loading}
            >
              SUBMIT
            </button>
            <button className="uss" id="upiclose" onClick={uclose}>
              CLOSE
            </button>
          </div>
          <div id="upis" className="uss">
            {message}
          </div>
        </div>
      )}
    </div>
  ) : (
    <div className="dmain">
      <div className="dbody">
        <div className="dh">DEPOSIT</div>
        <br />
        <button onClick={twenty} className="mdb">
          20
        </button>

        <button className="db" onClick={fifty}>
          50
        </button>
        <button className="db" onClick={hundred}>
          100
        </button>
        <br />
        <button onClick={fivehundred} className="mdb">
          500
        </button>
        <button className="db" onClick={thousand}>
          1000
        </button>
        <button className="db" onClick={twothousand}>
          2000
        </button>
      </div>
    </div>
  );
}
export default Dep;
