import React, { useRef, useState } from "react";
import "./css/log.css";
import axios from "axios";
function Log() {
  const suname=useRef(null);
  const spass=useRef(null);
  const sphone=useRef(null);
  const [message,setmessage]=useState();
  const sp=async(event)=>{
      event.preventDefault();
      await axios.post("http://localhost:4000/signup",{
        suname : suname.current.value,
        spass : spass.current.value,
        sphone : sphone.current.value,            
      }).then((res)=>{
        if(res.data==="permit"){
            console.log("permit");
            setmessage("permit");
        }
      })
  }
  return (
    <div className="lbody">
      <div className="main">
        <input type="checkbox" id="chk" aria-hidden="true"/>
        <div className="signup">
          <form>
            <label htmlFor="chk" aria-hidden="true">
              LOGIN
            </label>
            <input
              className="linput"
              type="text"
              name="username"
              placeholder="USER NAME"
              required=""
            />
            <input
              className="linput"
              type="password"
              name="password"
              placeholder="PASSWORD"
              required=""
            />
            <button id="lbutton">LOGIN</button>
          </form>
        </div>
        {message!=="permit"?<div className="login">
          <form onSubmit={sp}>
            <label htmlFor="chk" aria-hidden="true">
              SIGN UP
            </label>
            <input
              className="sinput"
              type="text"
              name="username"
              placeholder="USER NAME"
              required=""
              ref={suname}
            />
            <input
              className="sinput"
              type="password"
              name="password"
              placeholder="PASSWORD"
              required=""
              ref={spass}
            />
            <input
              className="sinput"
              type="number"
              name="phone"
              placeholder="PHONE NUMBER"
              required=""
              ref={sphone}
            />
            <button id="sbutton">SIGN UP</button>
            <div>{message}</div>
          </form>
        </div>:<div className="login">
          <form onSubmit={sp}>
            <label htmlFor="chk" aria-hidden="true">
              SIGN UP
            </label>
            <div id="amess" style={{color:'red',alignItems:'center'}}>{message}</div>
          </form>
        </div>}
      </div>
    </div>
  );
}
export default Log;
