import React, { useRef, useState } from "react";
import "../src/css/for.css";
import axios from "axios";
import firebase from "./firebaseconfig";
import { RecaptchaVerifier } from "firebase/auth";
import "firebase/auth";
function Fp() {
  const [go, setgo] = useState(true);
  const auth = firebase.auth();
  const us = useRef(null);
  const code = useRef(null);
  const [mess1, setmess1] = useState();
  const [mess, setmess] = useState();
  const [fp, setfp] = useState(false);
  const [phone, setphone] = useState();
  const [sphone, setsphone] = useState();
  const configurecaptcha = () => {
    window.recaptchaVerifier = new RecaptchaVerifier(auth, "sign-in-button", {
      size: "invisible",
      callback: (response) => {
        gotp();
      },
      defaultCountry: "IN",
    });
  };
  const gotp = (event) => {
    event.preventDefault();
    configurecaptcha();
    const phoneNumber = "+91" + phone;
    const appVerifier = window.recaptchaVerifier;
    firebase
      .auth()
      .signInWithPhoneNumber(phoneNumber, appVerifier)
      .then((confirmationResult) => {
        window.confirmationResult = confirmationResult;
        setmess1("OTP SENT SUCCESSFULL");
      })
      .catch((error) => {
        console.log(error.message);
      });
  };
  const uc = async (event) => {
    event.preventDefault();
    if (us.current.value === "") {
      setmess("FIELD IS EMPTY");
    } else {
      await axios
        .post("http://localhost:4000/foruc", {
          username: us.current.value,
        })
        .then((res) => {
          if (res.data.status === "sc") {
            let ld = res.data.phone;
            let sd = ld.toString();
            setsphone(sd.slice(-4));
            setphone(res.data.phone);
            setfp(true);
          } else {
            setmess("FAILED");
          }
        });
    }
  };
  const vo = (event) => {
    event.preventDefault();
    if (code.current.value === "") {
      setmess1("FIELD IS EMPTY");
    } else {
      const c = code.current.value;
      window.confirmationResult
        .confirm(c)
        .then((v) => {
          if (typeof v === "object") {
            setmess1("verified");
          }
        })
        .catch((error) => {
          setmess1("INCORRECT OTP");
          console.log(error.message);
        });
    }
  };
  return (
    <div className="formain">
      {fp ? (
        <div className="fpv">
          <div id="fpt">
            THE OTP WILL BE SENT TO THIS MOBILE NUMBER XXXX XX
            {sphone}
          </div>
          <div className="fpvb">
            {go ? (
              <div id="osend">OTP has been sent</div>
            ) : (
              <button onClick={gotp}>GET OTP</button>
            )}
          </div>
          <input
            className="fpvb"
            type="text"
            placeholder="ENTER OTP"
            ref={code}
          />
          <div className="fpvb">
            <button onClick={vo}>VERIFY</button>
          </div>
          <div id="fpvm">{mess1}</div>
          <div id="sign-in-button" style={{ display: "block" }}></div>
        </div>
      ) : (
        <form onSubmit={uc}>
          <div className="forbody">
            <input
              className="sm"
              type="text"
              placeholder="ENTER YOUR USERNAME"
              ref={us}
            />
            <div id="forbut" className="sm">
              <button type="submit" onClick={uc}>
                SUBMIT
              </button>
            </div>
            <div className="sm" id="formess">
              {mess}
            </div>
          </div>
        </form>
      )}
    </div>
  );
}
export default Fp;
