import React, { useRef, useState } from "react";
import "./css/log.css";
import axios from "axios";
import { useNavigate } from "react-router-dom";
function Log() {
  const navigate = useNavigate();
  const suname = useRef(null);
  const spass = useRef(null);
  const sphone = useRef(null);
  const luname = useRef(null);
  const lpass = useRef(null);
  const [message1, setmessage1] = useState();
  const [message2, setmessage2] = useState();
  const [acc, setacc] = useState();
  const [cuser, setcuser] = useState();
  const [lmess, setlmess] = useState();
  const [ptype, setptype] = useState(false);
  const [lptype, setlptype] = useState(false);
  const [sps, setsps] = useState(false);
  const [lsps, setlsps] = useState(false);
  const [otp, setotp] = useState(true);
  const go = (event) => {
    event.preventDefault();
    setotp(false);
  };
  const pshow = (event) => {
    event.preventDefault();
    setptype(true);
    setsps(true);
  };
  const phide = (event) => {
    event.preventDefault();
    setptype(false);
    setsps(false);
  };
  const lpshow = (event) => {
    event.preventDefault();
    setlptype(true);
    setlsps(true);
  };
  const lphide = (event) => {
    event.preventDefault();
    setlptype(false);
    setlsps(false);
  };
  const lp = async (event) => {
    event.preventDefault();
    if (luname.current.value !== "") {
      if (lpass.current.value !== "") {
        await axios
          .post("http://localhost:4000/login", {
            luname: luname.current.value,
            lpass: lpass.current.value,
          })
          .then((res) => {
            const j = res.data;
            console.log(j);
            if (j.name === "bc") {
              sessionStorage.setItem("un", luname.current.value);
              sessionStorage.setItem("ud", j.uuid);
              navigate("/home");
            }
            if (j.name === "ip") {
              setlmess("INCORRECT PASSWORD");
            }
            if (j.name === "iu") {
              setlmess("INCORRECT USERNAME");
            }
            if (j.name === "bic") {
              setlmess("BOTH ARE INCORRECT");
            }
          });
      } else {
        setlmess("FIELD IS EMPTY");
      }
    } else {
      setlmess("FIELD IS EMPTY");
    }
  };
  const cu = async (event) => {
    event.preventDefault();
    if (suname.current.value !== "") {
      await axios
        .post("http://localhost:4000/cu", {
          suname: suname.current.value,
        })
        .then((res) => {
          if (res.data === "a") {
            setcuser("USERNAME IS AVAILABLE");
          } else {
            setcuser("USERNAME IS NOT AVAILABLE");
          }
        });
    } else {
      setcuser("FIELD IS EMPTY");
    }
  };
  const sp = async (event) => {
    event.preventDefault();
    if (suname.current.value !== "") {
      if (spass.current.value !== "") {
        if (sphone.current.value !== "") {
          await axios
            .post("http://localhost:4000/signup", {
              suname: suname.current.value,
              spass: spass.current.value,
              sphone: sphone.current.value,
            })
            .then((res) => {
              if (res.data === "permit") {
                console.log("permit");
                setacc("permit");
                setmessage1("SIGN-UP SUCCESSFULL");
                setmessage2("GO TO LOGIN");
              }
              if (res.data === "up") {
                setacc("notpermit");
                setmessage1("BOTH USERNAME AND");
                setmessage2("PHONE NUMBER ALREADY EXIST");
              }
              if (res.data === "u") {
                setacc("notpermit");
                setmessage1("USERNAME IS");
                setmessage2("ALREADY EXIST");
              }
              if (res.data === "p") {
                setacc("notpermit");
                setmessage1("PHONE NUMBER IS");
                setmessage2("ALREADY EXIST");
              }
            });
        } else {
          setmessage1("SOME FIELD");
          setmessage2("IS EMPTY");
        }
      } else {
        setmessage1("SOME FIELD");
        setmessage2("IS EMPTY");
      }
    } else {
      setmessage1("SOME FIELD");
      setmessage2("IS EMPTY");
    }
  };
  return (
    <div className="lbody">
      <div className="main">
        <input type="checkbox" id="chk" aria-hidden="true" />
        <div className="signup">
          <form onSubmit={lp}>
            <label htmlFor="chk" aria-hidden="true">
              LOGIN
            </label>
            <input
              className="linput"
              type="text"
              name="username"
              placeholder="USER NAME"
              required=""
              ref={luname}
            />
            <input
              className="linput"
              type={lptype ? "text" : "password"}
              name="password"
              placeholder="PASSWORD"
              required=""
              ref={lpass}
            />
            {lsps ? (
              <button id="la" onClick={lphide}>
                HIDE
              </button>
            ) : (
              <button id="la" onClick={lpshow}>
                SHOW
              </button>
            )}
            <button id="lbutton">LOGIN</button>
            <div className="amess">{lmess}</div>
          </form>
        </div>
        {acc !== "permit" ? (
          <div className="login">
            <form onSubmit={sp}>
              <label htmlFor="chk" aria-hidden="true">
                SIGN UP
              </label>
              <input
                id="suinput"
                type="text"
                name="username"
                placeholder="USER NAME"
                required=""
                ref={suname}
              />
              <button id="ca" onClick={cu}>
                CHECK
              </button>
              <h6 id="am">{cuser}</h6>
              <input
                id="spinput"
                type={ptype ? "text" : "password"}
                name="password"
                placeholder="PASSWORD"
                required=""
                ref={spass}
              />
              {sps ? (
                <button id="ca" onClick={phide}>
                  HIDE
                </button>
              ) : (
                <button id="ca" onClick={pshow}>
                  SHOW
                </button>
              )}
              {otp ? (
                <input
                  id="sninput"
                  type="number"
                  name="phone"
                  placeholder="PHONE NUMBER"
                  required=""
                  ref={sphone}
                />
              ) : (
                <input
                  id="sninput"
                  type="number"
                  name="phone"
                  placeholder="ENTER OTP"
                  required=""
                  ref={sphone}
                />
              )}
              {otp ? (
                <button id="ca" onClick={go}>
                  GET OTP
                </button>
              ) : (
                <button id="ca">SUBMIT OTP</button>
              )}

              <button id="sbutton">SIGN UP</button>
              <div className="emess">
                {message1} {message2}
              </div>
            </form>
          </div>
        ) : (
          <div className="login">
            <form onSubmit={sp}>
              <label htmlFor="chk" aria-hidden="true">
                SIGN UP
              </label>
              <div className="amess">
                {message1}
                <br></br>
                {message2}
              </div>
            </form>
          </div>
        )}
      </div>
    </div>
  );
}
export default Log;
