import React, { useRef, useState } from "react";
import "../src/css/bgmi.css";
import axios from "axios";
import "../src/css/pay.css";
function Bgmi() {
  const [jstate, setjstate] = useState(false);
  const [pamount, setpamount] = useState();
  const [gtype, setgtype] = useState();
  const [jbn, sjbn] = useState(false);
  const gname = useRef(null);
  const pc = () => {
    setjstate(false);
  };
  const pay = async () => {
    if (gtype === "bsolo1") {
      const un = sessionStorage.getItem("un");
      await axios.post("http://localhost:4000/bsolo1", {
        username: un,
        gamename: gname.current.value,
      });
    }
  };
  const bsolo1 = async () => {
    setpamount("10");
    setjstate(true);
    setgtype("bsolo1");
  };
  const bsolo2 = async () => {
    window.location.reload(false);
    const un = sessionStorage.getItem("un");
    await axios.post("http://localhost:4000/bsolo2", {
      username: un,
    });
  };
  const bsquad = async () => {
    window.location.reload(false);
    const un = sessionStorage.getItem("un");
    await axios.post("http://localhost:4000/bsquad", {
      username: un,
    });
  };
  const bs1 = {
    type: "SOLO",
    map: "ERANGLE",
    date: "01/01/2004",
    time: "10:00 am",
  };
  const bs2 = {
    type: "SOLO",
    map: "ERANGLE",
    date: "01/01/2004",
    time: "11:00 am",
  };
  const bsd = {
    type: "SQUAD",
    map: "ERANGLE",
    date: "01/01/2004",
    time: "12:00 am",
  };
  return jstate ? (
    <div id="pmain">
      <div id="jp">
        <div id="tc">
          <h2 id="phead">TERMS AND CONDITIONS</h2>
          <button id="pc" onClick={pc}>
            X
          </button>
        </div>
        <div id="jb">
          <input id="ptext" type="text" placeholder="GAME NAME" ref={gname} />
          <button id="pay" onClick={pay}>
            PAY={pamount}
          </button>
          <div id="pstatus">hi</div>
        </div>
      </div>
    </div>
  ) : (
    <div className="bmain">
      <div className="bdiv" id="bsolo">
        <div className="bimage"></div>
        <div className="bdown1">
          <h4>
            TYPE : <span>{bs1.type}</span>
          </h4>
          <h4>
            MAP : <span>{bs1.map}</span>
          </h4>
          <h4>
            DATE : <span>{bs1.date}</span>
          </h4>
          <h4>
            TIME : <span>{bs1.time}</span>
          </h4>
        </div>
        <div className="bdown2">
          <div id="bp">
            <div id="ibp" style={{ width: "55%" }}></div>
          </div>
          <h4 id="bpn">1/100</h4>
          {jbn ? (
            <button className="bjoin" onClick={bsolo1}>
              JOIN
            </button>
          ) : (
            <div id="bjoined">JOINED</div>
          )}
        </div>
      </div>
      <div className="bdiv" id="bduo">
        <div className="bimage"></div>
        <div className="bdown1">
          <h4>
            TYPE : <span>{bs2.type}</span>
          </h4>
          <h4>
            MAP : <span>{bs2.type}</span>
          </h4>
          <h4>
            DATE : <span>{bs2.type}</span>
          </h4>
          <h4>
            TIME : <span>{bs2.type}</span>
          </h4>
        </div>
        <div className="bdown2">
          <div id="bp">
            <div id="ibp" style={{ width: "55%" }}></div>
          </div>
          <h4 id="bpn">1/100</h4>
          <button className="bjoin" onClick={bsolo2}>
            JOIN
          </button>
        </div>
      </div>
      <div className="bdiv" id="bsquad">
        <div className="bimage"></div>
        <div className="bdown1">
          <h4>
            TYPE : <span>{bsd.type}</span>
          </h4>
          <h4>
            MAP : <span>{bsd.type}</span>
          </h4>
          <h4>
            DATE : <span>{bsd.type}</span>
          </h4>
          <h4>
            TIME : <span>{bsd.type}</span>
          </h4>
        </div>
        <div className="bdown2">
          <div id="bp">
            <div id="ibp" style={{ width: "55%" }}></div>
          </div>
          <h4 id="bpn">1/100</h4>
          <button className="bjoin" onClick={bsquad}>
            JOIN
          </button>
        </div>
      </div>
    </div>
  );
}
export default Bgmi;
