import React, { useRef, useState } from "react";
import axios from "axios";
import "../src/css/wit.css";
import "../src/css/witc.css";
import Load from "./Load";
function Wit() {
  const [messagec, setmessagec] = useState();
  const [message,setmessage]=useState();
  const [loading, setLoading] = useState(false);
  const [wc, setwc] = useState(false);
  const wamnt = useRef();
  const wupi = useRef();
  const wac = async () => {
    try {
      setLoading(true);
      const un = sessionStorage.getItem("un");
      await axios
        .post("http://localhost:4000/withdraw", {
          username: un,
          upiid : wupi.current.value,
          amount: wamnt.current.value,
        })
        .then((res) => {
          if (res.data.status === "ib") {
            setmessagec("INSUFFICIENT BALANCE");
          }
          if (res.data.status === "success") {
            setmessagec("PAYOUT SUCCESSFULL");
          } else {
            setmessagec(res.data.status);
          }
        });
    } catch (error) {
      setmessagec(error.message);
    } finally {
      setLoading(false);
    }
  };
  const wa = async () => {
    if (wupi.current.value === "") {
      if (wamnt.current.value === "") {
        setmessage("SOME FIELD IS EMPTY");
      } else {
        setmessage("SOME FIELD IS EMPTY");
      }
    } else {
      if (wamnt.current.value === "") {
        setmessage("SOME FIELD IS EMPTY");
      } else {
        if (wamnt.current.value < 20) {
          setmessage("MINIMUM MITHDRAW IS 20");
        } else {
          setwc(true);
        }
      }
    }
  };
  const wacn=()=>{
    setwc(false);
  }
  return wc ? (
    <div className="wcmain">
      {loading ? (
        <Load />
      ) : (
        <div className="wcbody">
          <div className="cynm" id="cnf">ARE YOU SURE TO WITHDRAW <span>{wamnt.current.value}</span> ?</div>
          <button onClick={wac} id="wy" className="cynm">
            YES
          </button>
          <button className="cynm" id="wno"onClick={wacn}>NO</button>
          <div className="cynm" id="wcm">{messagec}</div>
        </div>
      )}
    </div>
  ) : (
    <div className="wmain">
      <div className="wbody">
        <div className="wh">WITHDRAW</div>
        <input
          id="wupi"
          type="text"
          placeholder="ENTER UPI ID "
          className="lass"
          ref={wupi}
        />
        <div id="we" className="lass">
          <span>Eg : </span>123456789@paytm
        </div>
        <input
          className="lass"
          type="number"
          placeholder="ENTER THE AMOUNT"
          ref={wamnt}
        />
        <button className="lass" id="wsubmit" onClick={wa}>
          SUBMIT
        </button>
        <div className="lass" id="wstatus">
          {message}
        </div>
      </div>
    </div>
  );
}
export default Wit;
