import React, { useRef, useState } from "react";
import "../src/css/for.css";
import axios from "axios";
import firebase from "./firebaseconfig";
import { RecaptchaVerifier } from "firebase/auth";
import "firebase/auth";
function Fp() {
  const us = useRef(null);
  const [mess, setmess] = useState();
  const [fp, setfp] = useState(false);
  const [phone, setphone] = useState();
  const configurecaptcha = () => {
    window.recaptchaVerifier = new RecaptchaVerifier(auth, "sign-in-button", {
      size: "invisible",
      callback: (response) => {
        onSignInSubmit();
      },
      defaultCountry: "IN",
    });
  };
  const uc = async (event) => {
    event.preventDefault();
    if (us.current.value === "") {
      setmess("FIELD IS EMPTY");
    } else {
      await axios
        .post("http://localhost:4000/foruc", {
          username: us.current.value,
        })
        .then((res) => {
          if (res.data.status === "sc") {
            console.log(res.data.phone);
            setphone(res.data.phone);
            configurecaptcha();
            const phoneNumber = "+91" + phone;
            const appVerifier = window.recaptchaVerifier;
            firebase
              .auth()
              .signInWithPhoneNumber(phoneNumber, appVerifier)
              .then((confirmationResult) => {
                window.confirmationResult = confirmationResult;
                setmess("OTP SENT SUCCESSFULL");
              })
              .catch((error) => {
                console.log(error.message);
              });
          } else {
            setmess("FAILED");
          }
        });
    }
  };
  return (
    <div className="formain">
      {fp ? (
        <div className="fpv">
          <input type="text" placeholder="ENTER OTP" />
          <div id="fpvb">
            <button>VERIFY</button>
          </div>
        </div>
      ) : (
        <form onSubmit={uc}>
          <div className="forbody">
            <input
              className="sm"
              type="text"
              placeholder="ENTER YOUR USERNAME"
              ref={us}
            />
            <div id="forbut" className="sm">
              <button type="submit" onClick={uc}>
                SUBMIT
              </button>
            </div>
            <div className="sm" id="formess">
              {mess}
            </div>
          </div>
        </form>
      )}
    </div>
  );
}
export default Fp;
