import React, { useRef, useState } from "react";
import axios from "axios";
import { Elements } from '@stripe/react-stripe-js';
import { loadStripe } from '@stripe/stripe-js';
import { useStripe } from '@stripe/react-stripe-js';
import "../src/css/dep.css";
import "../src/css/upi.css";
function Dep() {
  const stripe = useStripe();
  const [loading, setLoading] = useState(false);
  const [amount, setamount] = useState();
  const [upi, setupi] = useState(false);
  const upiid = useRef();
  const stripePromise = loadStripe('pk_live_51OR8kiSIm5G6fCOHDjlmxpsPuTlojur8gyaQZxaZZbwDNXwEljzSfqHNXjhVzyyDGaJ3eEfPlvcrTt9x5xYUVxy000qsIE7HRP');
  const upipayment =  async() => {
  setLoading(true);
  try {
    const response = await axios.post('http://localhost:4000/payment');
    const sessionId = response.data.id;

    const result = await stripe.redirectToCheckout({ sessionId });

    if (result.error) {
      console.error(result.error.message);
    }
  } catch (error) {
    console.error(error.message);
  } finally {
    setLoading(false);
  }
  
    
  };
  const twenty = () => {
    setupi(true);
    setamount(20);
  };
  const fifty = () => {
    setupi(true);
    setamount(50);
  };
  const hundred = () => {
    setupi(true);
    setamount(100);
  };
  const fivehundred = () => {
    setupi(true);
    setamount(500);
  };
  const thousand = () => {
    setupi(true);
    setamount(1000);
  };
  const twothousand = () => {
    setupi(true);
    setamount(2000);
  };
  const uclose = () => {
    setupi(false);
  };
  return upi ? (
    <Elements stripe={stripePromise}>
    <div className="upi">
      <div className="iupi">
        <form>
          <div id="upia">
            AMOUNT : <span>{amount}</span>
          </div>
          <div>
            <input
              className="uss"
              type="text"
              placeholder="ENTER YOUR UPI ID"
              ref={upiid}
            />
          </div>
          <div>
            <button
              type="submit"
              className="uss"
              id="upisubmit"
              onClick={upipayment} disabled={loading}
            >
              SUBMIT
            </button>
            <button className="uss" id="upiclose" onClick={uclose}>
              CLOSE
            </button>
          </div>
          <div id="upis" className="uss">
            STATUS
          </div>
        </form>
      </div>
    </div></Elements>
  ) : (
    <div className="dmain">
      <div className="dbody">
        <div className="dh">DEPOSIT</div>
        <br />
        <button onClick={twenty} className="mdb">
          20
        </button>

        <button className="db" onClick={fifty}>
          50
        </button>
        <button className="db" onClick={hundred}>
          100
        </button>
        <br />
        <button onClick={fivehundred} className="mdb">
          500
        </button>
        <button className="db" onClick={thousand}>
          1000
        </button>
        <button className="db" onClick={twothousand}>
          2000
        </button>
      </div>
    </div>
  );
}
export default Dep;
