import React, { useState } from "react";
import "./css/nav.css";
import { useNavigate } from "react-router-dom";
function Nav() {
  const navigate = useNavigate();
  const [isOpen, setIsOpen] = useState(false);
  const [dd, setdd] = useState(false);
  const dep = () => {
    navigate("/deposit");
  };
  const wit = () => {
    navigate("/withdraw");
  };
  const p = () => {
    setIsOpen(!isOpen);
  };
  const h = () => {
    navigate("/home");
  };
  const u = () => {
    navigate("/um");
  };
  const r = () => {
    navigate("/result");
  };
  const ndd = () => {
    setdd(!dd);
  };
  return (
    <div className="nmain">
      <div className="nav">
        <div id="nh1">GAMING HUB</div>
        <div className="ndd">
          <button id="ndd" onClick={ndd}>
            X
          </button>
        </div>
        {dd && (
          <div className="drop">
            <div className="idrop">
              <span>COIN=100</span>
            </div>
            <div className="idrop">
              <span onClick={h}>HOME</span>
            </div>
            <div className="idrop">
              <span onClick={u}>UPCOMMING MATCHES</span>
            </div>
            <div className="idrop">
              <span onClick={r}>RESULT</span>
            </div>
            <div className="idrop">
              <span onClick={dep}>DEPOSIT</span>
            </div>
            <div className="idrop">
              <span onClick={wit}>WITHDRAW</span>
            </div>
            <div className="idrop">
              <span>HELP-LINE</span>
            </div>
          </div>
        )}
        <div className="hur">
          <button onClick={h}>HOME</button>
          <button className="ur" onClick={u}>
            UPCOMMING MATCHES
          </button>
          <button className="ur" onClick={r}>
            RESULT
          </button>
        </div>
        <div className="p">
          <button onClick={p}>PROFILE</button>
        </div>
      </div>
      {isOpen && (
        <div className="dw">
          <button onClick={dep}>DEPOSIT</button>
          <button onClick={wit}>WITHDRAW</button>
          <button>HELP-LINE</button>
        </div>
      )}
    </div>
  );
}
export default Nav;
