import React, { useRef, useState } from "react";
import { loadStripe } from "@stripe/stripe-js";
import axios from "axios";
import "../src/css/dep.css";
import "../src/css/upi.css";
function Dep() {
  const [amount, setamount] = useState();
  const [upi, setupi] = useState(false);
  const [client_secret, setclient_secret] = useState();
  const stripePromise = loadStripe(
    "pk_test_51OR8kiSIm5G6fCOH1qQFbjSNZvv8qnS9ZEP6fMmJbeUCDKxN45UHHAEoajkpRjpNiO4Jn6s5Jl4FeUvbJEGr42QG00k4Oz2rEA"
  );
  const upiid = useRef();
  const upipayment = async (event) => {
    event.preventDefault();
    const stripe = await stripePromise;
    try {
      const un = sessionStorage.getItem("un");
      await axios
        .post("http://localhost:4000/payments", {
          amount: amount,
          username: un,
        })
        .then((res) => {
          setclient_secret(res.data);
        });
      const result = await stripe.confirmPa(client_secret, {
        payment_method: {
          upi: {
            id: upiid.current.value,
          },
        },
      });
      if (result.error) {
        console.log("UPI payment failed:", result.error.message);
      } else if (result.paymentIntent.status === "succeeded") {
        console.log("UPI payment successful!");
      }
    } catch (error) {
      console.log(error);
    }
  };
  const twenty = () => {
    setupi(true);
    setamount(20);
  };
  const fifty = () => {
    setupi(true);
    setamount(50);
  };
  const hundred = () => {
    setupi(true);
    setamount(100);
  };
  const fivehundred = () => {
    setupi(true);
    setamount(500);
  };
  const thousand = () => {
    setupi(true);
    setamount(1000);
  };
  const twothousand = () => {
    setupi(true);
    setamount(2000);
  };
  const uclose = () => {
    setupi(false);
  };
  return upi ? (
    <div className="upi">
      <div className="iupi">
        <form>
          <div id="upia">
            AMOUNT : <span>{amount}</span>
          </div>
          <div>
            <input
              className="uss"
              type="text"
              placeholder="ENTER YOUR UPI ID"
              ref={upiid}
            />
          </div>
          <div>
            <button
              type="submit"
              className="uss"
              id="upisubmit"
              onClick={upipayment}
            >
              SUBMIT
            </button>
            <button className="uss" id="upiclose" onClick={uclose}>
              CLOSE
            </button>
          </div>
          <div id="upis" className="uss">
            STATUS
          </div>
        </form>
      </div>
    </div>
  ) : (
    <div className="dmain">
      <div className="dbody">
        <div className="dh">DEPOSIT</div>
        <br />
        <button onClick={twenty} className="mdb">
          20
        </button>

        <button className="db" onClick={fifty}>
          50
        </button>
        <button className="db" onClick={hundred}>
          100
        </button>
        <br />
        <button onClick={fivehundred} className="mdb">
          500
        </button>
        <button className="db" onClick={thousand}>
          1000
        </button>
        <button className="db" onClick={twothousand}>
          2000
        </button>
      </div>
    </div>
  );
}
export default Dep;
