import React, { useRef, useState } from "react";
import axios from "axios";
import "../src/css/dep.css";
import "../src/css/upi.css";
import { useStripe, useElements, CardElement } from '@stripe/react-stripe-js';
function Dep() {
  const stripe = useStripe();
  const elements = useElements();
  const [amount, setamount] = useState();
  const [upi, setupi] = useState(false);
  const [clientSecret, setClientSecret] = useState();
  const upiid = useRef();
  const upipayment = async (event) => {
    event.preventDefault();
    if (!stripe || !elements) {
      return;
    }
    await axios.post("http://localhost:4000/payment",{
      amount:amount,
    }).then((res)=>{
      setClientSecret(res.data.clientSecret);
    });
    const result = await stripe.confirmCardPayment(clientSecret, {
      payment_method: {
        card: elements.getElement(CardElement),
        billing_details: {
          name:sessionStorage.getItem("un"),
        },
      },
    });

    if (result.error) {
      console.error(result.error.message);
    } else {
      console.log(result.paymentIntent);
    }
  };
  const twenty = () => {
    setupi(true);
    setamount(20);
  };
  const fifty = () => {
    setupi(true);
    setamount(50);
  };
  const hundred = () => {
    setupi(true);
    setamount(100);
  };
  const fivehundred = () => {
    setupi(true);
    setamount(500);
  };
  const thousand = () => {
    setupi(true);
    setamount(1000);
  };
  const twothousand = () => {
    setupi(true);
    setamount(2000);
  };
  const uclose = () => {
    setupi(false);
  };
  return upi ? (
    <div className="upi">
      <div className="iupi">
        <form>
          <div id="upia">
            AMOUNT : <span>{amount}</span>
          </div>
          <CardElement />
          {/* <div>
            <input
              className="uss"
              type="text"
              placeholder="ENTER YOUR UPI ID"
              ref={upiid}
            />
          </div> */}
          <div>
            <button
              type="submit"
              className="uss"
              id="upisubmit"
              onClick={upipayment}
              disabled={!stripe}
            >
              SUBMIT
            </button>
            <button className="uss" id="upiclose" onClick={uclose}>
              CLOSE
            </button>
          </div>
          <div id="upis" className="uss">
            STATUS
          </div>
        </form>
      </div>
    </div>
  ) : (
    <div className="dmain">
      <div className="dbody">
        <div className="dh">DEPOSIT</div>
        <br />
        <button onClick={twenty} className="mdb">
          20
        </button>

        <button className="db" onClick={fifty}>
          50
        </button>
        <button className="db" onClick={hundred}>
          100
        </button>
        <br />
        <button onClick={fivehundred} className="mdb">
          500
        </button>
        <button className="db" onClick={thousand}>
          1000
        </button>
        <button className="db" onClick={twothousand}>
          2000
        </button>
      </div>
    </div>
  );
}
export default Dep;
